import requests
import re

# koda vrne slovar vseh kontinentov z državami/območji, ki jim pripadajo (glede na to kako ima stran to razdeljeno)
url1 = 'https://a-z-animals.com/animals/location/'
html1 = requests.get(url1).text
z1 = re.findall(r'</h3><ul class="list-unstyled row"><li class="list-item col-md-4 col-sm-6">[^2]+', html1)
vsi_kontinenti = dict()
for j in range(len(z1)):
    kontinent = re.findall(r'</h3><ul class="list-unstyled row"><li class="list-item col-md-4 col-sm-6"><a href="/animals/location/[^/]+', z1[j])[0].replace('</h3><ul class="list-unstyled row"><li class="list-item col-md-4 col-sm-6"><a href="/animals/location/', '')
    if '-' in kontinent:
        kontinent = kontinent.split('-')
        kontinent = kontinent[0].capitalize() + '-' + kontinent[1].capitalize()
    else:
        kontinent = kontinent.capitalize()
    aa = z1[j].replace('</h3><ul class="list-unstyled row">', '').replace('<li class="list-item col-md-4 col-sm-6">', '').replace('<a href="/animals/location/', '').replace('/a></li></ul></section><section id="faqs"></section></div></div></article></main><div class="col-md-1', '').replace('</li>', '').replace('</ul><h', '').replace('">', '').split('/')
    v1 = []
    for i in range(len(aa)):
        if '<' in aa[i]:
            v1.append(aa[i][:-1])
    vsi_kontinenti[kontinent] = v1

# koda, ki iz slovarja s kontinenti in drzavami, ki spadajo pod njih naredi tabelo vseh drzav (ne glede na kontinent)
vse_drzave = []
for vrednosti in vsi_kontinenti.values():
    for i in range(len(vrednosti)):
        vse_drzave.append(vrednosti[i])
vse_drzave.sort()

# koda, ki iz enega url naslova pridobi kratice drzav, ki jih potrebujeva za risanje zemljevida (na katerih predelih sveta stanuje neka žival)
url1 = 'https://github.com/gsnaveen/plotly-worldmap-mapping-2letter-CountryCode-to-3letter-country-code/blob/master/countryMap.txt'
html1 = requests.get(url1).text
t1 = re.findall(r'class="blob-code blob-code-inner js-file-line">[^<]+', html1)
kratice_drzav = dict()
for j in range(1, len(t1)):
    s1 = t1[j].replace('class="blob-code blob-code-inner js-file-line">', '').replace('\r', '').split('\t')
    kratice_drzav[s1[0]] = s1[2]

# koda, ki preveri, ali imava kratice od vseh drzav
# oceane bova obravnavala kot celoto
oceani = ['Southern Ocean', 'Arctic', 'Great Lakes', 'Indian', 'Mediterranean-Caspian', 'North Atlantic', 'Northeast Pacific', 'Northwest Pacific', 'South Atlantic', 'Southeast Pacific', 'Southwest Pacific']
kljuci = []
kratice_dodaj = []
for kljuc1 in kratice_drzav.keys():
        kljuci.append(kljuc1)

for i in range(len(vse_drzave)):
    if vse_drzave[i] not in kljuci and vse_drzave[i] not in oceani:
        kratice_dodaj.append(vse_drzave[i])

# iz seznama drzav, katerim kratice program ni našel, izločiva tiste, ki imajo oklepaj v 'imenu', saj ta  zvezna drzava/otok/obmocje/... v oklepaju ze spada pod to drzavo, ki je izven oklepaja        
for k in range(len(kratice_dodaj)):
    if '(' in kratice_dodaj[k]:
        m = 0
        for l in range(len(kratice_dodaj[k])):
            if kratice_dodaj[k][l] == '(':
                m = l
                break
        kratice_dodaj[k] = kratice_dodaj[k][:m].strip()

# na zmanjsani tabeli preberimo, ce smo z zgornjo zanko razresili katerega od problemov
mankajoce_kratice = []
for k in range(len(kratice_dodaj)):
    l = 0
    for kljuc1 in kljuci:
        if kratice_dodaj[k] in kljuc1:
            l = 1
    if l == 0:
        mankajoce_kratice.append(kratice_dodaj[k])